import React, { useState } from 'react';

import Shelf from './Shelf';
import Card from '../UI/Card';
import GondulaFilter from './GondulaFilter';
import './Gondula.css';

const Gondula = (props) => {
  const [filteredYear, setFilteredYear] = useState('2020');

  const filterChangeHandler = selectedYear => {
    setFilteredYear(selectedYear);
  };

  return (
    <div>
      <Card className='Gondula'>
        <GondulaFilter selected={filteredYear} onChangeFilter={filterChangeHandler} />
        <Shelf
          id={props.items[0].id}
          title={props.items[0].title}
          amount={props.items[0].amount}
          product={props.items[0].product}
        />
        <Shelf
          id={props.items[1].id}
          title={props.items[1].title}
          amount={props.items[1].amount}
          product={props.items[1].product}
        />
        <Shelf
          id={props.items[2].id}
          title={props.items[2].title}
          amount={props.items[2].amount}
          product={props.items[2].product}
        />
        <Shelf
          id={props.items[3].id}
          title={props.items[3].title}
          amount={props.items[3].amount}
          product={props.items[3].product}
        />

      </Card>
    </div>
  );
};

export default Gondula;
