import React, { useCallback, useEffect, useState } from 'react';
// import NewExpense from './components/NewExpense/NewExpense';
import Gondola from './components/Gondula/Gondola';
const App = () => {

  const [planograms, setPlanograms] = useState([])
  const [isLoading, setIsloading] = useState(false);
  const [error, setError] = useState(null);
  // const gondula = [
  //   {
  //     id: 'e1',
  //     title: 'Paper',
  //     amount: 94.12,
  //     product: new Date(2020, 7, 14),
  //   },
  //   { id: 'e2',
  //     title: 'New TV', 
  //     amount: 799.49, 
  //     product: new Date(2021, 2, 12) },
  //   {
  //     id: 'e3',
  //     title: 'Car Insurance',
  //     amount: 294.67,
  //     product: new Date(2021, 2, 28),
  //   },
  //   {
  //     id: 'e4',
  //     title: 'New Desk (Wooden)',
  //     amount: 450,
  //     product: new Date(2021, 5, 12),
  //   },
  // ];

  const fetchPlanogramsHandler = useCallback(async () => {
    setIsloading(true);
    setError(null);
    try {
      const url = 'https://apiserver.sungalcorp.synology.me/dbGet_planogram?filter=storeID=7'
      // const response = await axios.get(url).then(async res=> {
      //   const facings = res.data;

      // if (!response.ok) {
      //   throw new Error('Something went wrong!');
      // }

      // .then((response)=>{console.log(response)});
      // const response = await fetch('https://swapi.dev/api/films/');
      const response = await fetch(url);

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }
      const facings =  await response.list();
      console.log(facings)
      setPlanograms(facings.data)

      const transformedPlanogram = facings.map(facing =>
        {

        return { 

          gondola: facing.gondola,
          shelf: facing.shelf,
          facing: facing.facing,
          upc: facing.upc
        };
      });

      setPlanograms(transformedPlanogram);
    // })
    console.log(planograms)




    } catch (error) {
      setError(error.message);
    }
    setIsloading(false);
  }, []);

  useEffect(() => {
    fetchPlanogramsHandler();
  }, [fetchPlanogramsHandler]);

  let content = <p>Found no planograms.</p>;

  if (planograms.length > 0) {
    content = <Gondola items={planograms} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <React.Fragment>

      <section>
        <button onClick={fetchPlanogramsHandler}>FetchPlanogram</button>
      </section>
      <section>{content}</section>
      {/* <div>
        <Gondula planograms={planograms} />
      </div> */}
    </React.Fragment>
  )

  // return (
  //   <div>
  //     <Gondula items={gondula} />
  //   </div>
  // );
}

export default App;
