import React, { useState } from 'react';

import Card from '../UI/Card';
import './Gondola.css'

import GondolaFilter from './GondolaFilter';
import ShelfFilter from './ShelfFilter';

import Facing from './Facing';
import Shelf from './Shelf';

const Gondola = (props) => {

  const [filteredGondula, setFilteredGondula] = useState('Gond-1');
  const filterChangeHandler = selectedGondula => {
    setFilteredGondula(selectedGondula);
  };
  const filteredShelfs = props.items.filter(shelf => {
    return shelf.gondola === filteredGondula;
  })


  const [filteredShelves, setFilteredShelf] = useState('1');
  const shelfFilterChangeHandler = selectedShelf => {
    setFilteredShelf(selectedShelf);
  };
  const filteredFacings = props.items.filter(facing => {
    return facing.shelf === filteredShelfs;
  })

  return (
    <div>
      <Card className='Gondola'>
        <GondolaFilter selected={filteredGondula} onChangeFilter={filterChangeHandler} />
        <ShelfFilter selected={filteredShelves} onChangeFilter={filterChangeHandler} />
        { filteredFacings.map((planogram) => (
        <Facing
          upc={planogram.upc}
          shelf={planogram.shelf}
          gondola={planogram.gondola}
        />
        ))}
      </Card>


    </div>
  );
};

export default Gondola;
