import React, { useCallback, useEffect, useState } from 'react';
// import NewExpense from './components/NewExpense/NewExpense';
import Gondola from './components/Gondula/Gondola';
const App = () => {

  const [planograms, setPlanograms] = useState([])
  const [isLoading, setIsloading] = useState(false);
  const [error, setError] = useState(null);

  const fetchPlanogramsHandler = useCallback(async () => {
    setIsloading(true);
    setError(null);
    try {
      // const url = 'https://apiserver.sungalcorp.synology.me/dbGet_planogram?filter=storeID=7'
      const url = 'https://apiserver.sungalcorp.synology.me/dbGet_displaymatrixforweb?filter=storeID=7'

      // const response = await fetch('https://swapi.dev/api/films/');
      const response = await fetch(url);

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }
      const facings =  await response.json();
      console.log(facings)
      // setPlanograms(facings.data)

    const transformedPlanogram =  facings && facings.map(facing => {

      // console.log(facing.UPC)
      return {
        gondola: facing.displayfixtureID,
        shelf: facing.shelf,
        facing: facing.facing,
        upc: facing.UPC,
      };


      });
    setPlanograms(transformedPlanogram);

    console.log(planograms)

    } catch (error) {
      setError(error.message);
    }
    setIsloading(false);
  }, []);

  useEffect(() => {
    fetchPlanogramsHandler();
  }, [fetchPlanogramsHandler]);

  let content = <p>Found no planograms.</p>;

  if (planograms.length > 0) {
    content = <Gondola items={planograms} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <React.Fragment>

      <section>
        <button onClick={fetchPlanogramsHandler}>FetchPlanogram</button>
      </section>
      <section>{content}</section>

    </React.Fragment>
  )

}

export default App;
