import Gondula from './components/Gondula/Gondula';

const App = () => {

  const [planograms, setPlanograms] = useState([])
  const [isLoading, setIsloading] = useState(false);
  const [error, setError] = useState(null);
  const gondula = [
    {
      id: 'e1',
