    },
    {
      id: 'e4',
      title: 'New Desk (Wooden)',
      amount: 450,
      product: new Date(2021, 5, 12),
    },
  ];

  const fetchPlanogramsHandler = useCallback(async () => {
    setIsloading(true);
    setError(null);
    try {
      const response = await fetch('https://apiserver.sungalcorp.synology.me/dbGet_planogram?filter=storeID=7');
      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await response.json();

      const transformedPlanogram = data.results.map(planogramData => {
        return { 
          gondula: planogramData.gondula,
          shelf: planogramData.shelf,
          facing: planogramData.facing,
          upc: planogramData.upc
        };
      });
      setPlanograms(transformedPlanogram);
    } catch (error) {
      setError(error.message);
    }
    setIsloading(false);
  }, []);

