import React from 'react';
import './Facing.css';

const getPercentStyle = (percent) => {
  let styleObj = { backgroundColor: '',
  //  width:''
   };
  //alert(getPercentColor(percent))
  styleObj.backgroundColor = getPercentColor(percent);
  // styleObj.width = percent;
  return styleObj;
};




export const getPercentColor = (percent) => {
  //alert("percent="+percent)
  percent = percent.replace(/%/g, '');
  let red = '#ff0000';
  let orange = '#ff9900';
  let yellow = '#fdfa06';
  let white = '#ffffff';
  let green = '#00ff00';
  let purple = '#ff00ff';

  if (isNaN(percent) || percent > 90){

    return white;
  } 

  if (percent > 80) {

    return purple;
  }

  if (percent > 60) {

    return green;
  }

  if (percent > 40) {

    return yellow;
  }

  if (percent > 20) {

    return orange;
  }

  return red;
};

const Facing = (props) => {
console.log(props)
const facing = props.facing 

const getPercent = (stateVal) => {
  try {
    let valStr = (stateVal + '-').split('-')[0];
    return (
      Math.round((valStr.split('1').length - 1 / valStr.length) * 10) + '%'
    );
  } catch (err) {
    return ('NA');
  }
};
var prodPercentage = getPercent(facing.state) 

var prodImage

try{
  prodImage = require('../imgs/'+ facing.productUPC+'.png')
}catch(err){
  prodImage = require('../imgs/blackbox.png')
  console.log("image not found error trapped")
}


var percentageStyle = getPercentStyle(prodPercentage);



return (
    <li className="facing-info">
    <img  
         className="facing-info__upc" 
         src={prodImage} 
         alt = 'ProductImg'
    />

    <div class="percentage-tag" style={percentageStyle}>
          {prodPercentage}
    {/* <div class="percentage-div">
        <div class="percentageBackground-div" style={percentageStyle}>

        </div>
        </div> */}
    </div>

    </li>
);




};
export default Facing;
