import React, { useState } from 'react';


import './Facing.css';

const Facing = (props) => {


  const [singleFacing, setSingleFacing] = useState([])
  setSingleFacing(props);

  console.log("11111",singleFacing)


  // const shelf = props.shelf.toLocaleString('en-US', { shelf: 'long' });
  // const upc = props.upc.toLocaleString('en-US', {upc});
  // const gondola = props.gondola.toLocaleString('en-US', { gondola: '2-digit' })

  return (


      <li className="facing-info">
    {singleFacing && singleFacing.map(item => 
      <h2 className="facing-info__shelf">{'ShelfID:'+item.shelfID}</h2>
    )}

      <img  
      className="facing-info__upc" 
      src={require('./imgs/'+singleFacing.productUPC+'.png')} 
      alt = 'ProductImg'
      />
      <p className="facing-info__gondola">{singleFacing.displayFixtureID}</p>
    </li>


  );

};

export default Facing;
