import React, { useState } from 'react';

import Card from '../UI/Card';
import './Gondola.css'
import Shelf from './Shelf'
import GondolaFilter from './GondolaFilter';

const Gondola = (props) => {

  const [filteredGondola, setFilteredGondola] = useState("0");
  const filterChangeHandler = selectedGondola => {
    setFilteredGondola(selectedGondola);
  };

  console.log(props.items)
  console.log("filteredGondola",filteredGondola)
  const filteredShelfs = props.items.filter((item,index) => index === filteredGondola);

  console.log('filteredGondola:',filteredShelfs)

  return (
    <div>
      <Card className='Gondola'>
        <GondolaFilter selected={filteredGondola} onChangeFilter={filterChangeHandler} />
        {filteredShelfs[0] && filteredShelfs[0].map((item,index) => (
        <Shelf
          key={index}
          shelf={item}
        />
        ))}
      </Card>
    </div>
  );
};
export default Gondola;
