import React, { useState } from 'react';

import Card from '../UI/Card';
import './Gondola.css'
import Shelf from './Shelf'
import GondolaFilter from './GondolaFilter';

import Facing from './Facing';
const Gondola = (props) => {

  const [filteredGondula, setFilteredGondula] = useState('0');
  const filterChangeHandler = selectedGondula => {
    setFilteredGondula(selectedGondula);
  };
  const filteredShelfs = props.items.filter(shelf => {
    // return shelf.gondolaID === filteredGondula;
    // return shelf.gondolaID === shelf[filteredGondula];
    return shelf[filteredGondula];
  })
  // function passShelf() {}
  return (
    <ShelfComponent shelfs= {filteredShelfs}/>
  )
  // const shelfFilterChangeHandler = selectedShelf => {
  //   setFilteredGondula(selectedShelf);
  // };
  // const filteredFacings = props.items.filter(facing => {
  //   return facing.shelf === filteredShelfs;
  // })
  console.log('filteredGondola:',filteredGondula)

  return (
    <div>
      <Card className='Gondola'>
        <GondolaFilter selected={filteredGondula} onChangeFilter={filterChangeHandler} />
        {/* <ShelfFilter selected={filteredShelfs} onChangeFilter={shelfFilterChangeHandler} /> */}
        { filteredShelfs && filteredShelfs.map((planogram) => (
        <Shelf
          key={planogram.facingID}
          shelfID={planogram.shelfID}
          gondolaID={planogram.displayFixtureID}
        />
        ))}

      </Card>


    </div>
  );
};

export default Gondola;
