import React, { useState } from 'react';

import Card from '../UI/Card';
import './Gondola.css'
import GondolaFilter from './GondolaFilter';
import Facing from './Facing';
import Shelf from './Shelf';

const Gondola = (props) => {
  const [filteredGondula, setFilteredGondula] = useState('Gond-1');

  const filterChangeHandler = selectedGondula => {
    setFilteredGondula(selectedGondula);
  };

  const filteredShelfs = props.items.filter(facing => {
    return facing.gondola === filteredGondula;
  })

  return (
    <div>
      <Card className='Gondola'>
        <GondolaFilter selected={filteredGondula} onChangeFilter={filterChangeHandler} />
        { filteredShelfs.map((planogram) => (
        <Facing
          upc={planogram.upc}
          shelf={planogram.shelf}
          gondola={planogram.gondola}
        />
        ))}
      </Card>


    </div>
  );
};

export default Gondola;
