import React, { useState } from 'react';

import Shelf from './Shelf';
import Card from '../UI/Card';
import GondulaFilter from './GondulaFilter';
import './Gondula.css';

const Gondula = (props) => {
  const [filteredYear, setFilteredYear] = useState('2020');

  const filterChangeHandler = selectedYear => {
    setFilteredYear(selectedYear);
  };

  return (
    <div>
      <Card className='Gondula'>
        <GondulaFilter selected={filteredYear} onChangeFilter={filterChangeHandler} />
        <Shelf
          upc={props.upc}
          shelf={props.shelf}
          // title={props.items[0].title}
          // amount={props.items[0].amount}
          // product={props.items[0].product}
        />
      </Card>
    </div>
  );
};

export default Gondula;
