import React from 'react';

import './Facing.css';

const Facing = (props) => {
  // const shelf = props.shelf.toLocaleString('en-US', { shelf: 'long' });
  // const upc = props.upc.toLocaleString('en-US', {upc});
  // const gondola = props.gondola.toLocaleString('en-US', { gondola: '2-digit' })
  return (
    <li className="facing-info">
      <h2 className="facing-info__shelf">{'ShelfID:'+props.shelfID}</h2>
      <img  
      className="facing-info__upc" 
      src={require('./imgs/'+props.productUPC+'.png')} 
      alt = 'ProductImg'
      />
      <p className="facing-info__gondola">{props.displayFixtureID}</p>
    </li>
  );

};

export default Facing;
