import React, { useState } from 'react';

import './Shelf.css'
import Facing from './Facing';
import Card from '../UI/Card';
import GondolaFilter from './GondolaFilter';
const Shelf = (props) => {


  const [filteredGondula, setFilteredGondula] = useState('0');
  const filterChangeHandler = selectedGondula => {
    setFilteredGondula(selectedGondula);
  };
  const filteredShelfs = props.items.filter(shelf => {
    // return shelf.gondolaID === filteredGondula;
    // return shelf.gondolaID === shelf[filteredGondula];
    return shelf[filteredGondula];
  })

  return (
    <Card className='shelf'>
      <GondolaFilter selected={filteredGondula} onChangeFilter={filterChangeHandler} />

      {filteredShelfs && filteredShelfs.map((planogram) => (
        <Facing 
          productUPC={planogram.productUPC} 
          facingID={planogram.facingID}
          // shelfID={planogram.shelfID}
          // gondolaID={planogram.gondolaID}
        />
      ))}

    </Card>

  );
};

export default Shelf;
