import React from 'react';

import './Facing.css';

const Facing = (props) => {
  const month = props.date.toLocaleString('en-US', { month: 'long' });
  const day = props.date.toLocaleString('en-US', { day: '2-digit' });
  const year = props.date.getFullYear();

  return (
    <div className='facing-date'>
      <div className='facing-date__month'>{month}</div>
      <div className='facing-date__year'>{year}</div>
      <div className='facing-date__day'>{day}</div>
    </div>
  );
};

export default Facing;
