import React from 'react';
import './Facing.css';

const Facing = (props) => {
console.log(props)
const facing = props.facing 

const facingLength = ((facing.state.split(/[\s-]+/)[0]).match((/1/g)||[]).length)
const itemLength = (facing.state.split(/[\s-]+/)[0]).length
console.log(facingLength)
console.log(itemLength)

var prodImage
var prodPercentage =  (100 * itemLength) / facingLength
console.log(prodPercentage)
try{
  prodImage = require('../imgs/'+ facing.productUPC+'.png')
}catch(err){
  prodImage = require('../imgs/blackbox.png')
  console.log("image not found error trapped")
}

try{
  prodPercentage = require(prodPercentage+'%')
}catch(err){
  prodPercentage = require()
  console.log("image not found error trapped")
}

return (
    <li className="facing-info">
    <img  
         className="facing-info__upc" 
         src={prodImage} 
         alt = 'ProductImg'
    />
  <div class="percentage">
  <div class="color" >{prodPercentage}</div>
  </div>
    </li>
);




};
export default Facing;
