import React, { useCallback, useEffect, useState } from 'react';

import Client from '../components/Client';
import { getPercentColor } from '../lib/PercentColor';
var logoImage = require('../imgs/LIVINGPOG.png')
var SungalImage = require('../imgs/SUNGAL.png')


// const App = () => {
const LivingPOG = () => {


// fetch data
  const [planogram, setPlanogram] = useState([])
  const [isLoading, setIsloading] = useState(false);
  const [error, setError] = useState(null);

  const transformPOGData = (data) =>{
    /* transforms data to:

    (clientlevel)[
                  (storeLevel)[
                               (gondolaLevel)[
                                              (shelfLevel)[ 
                                                           {facing},{facing}
                                                           ]
                                              ]
                                ]
                  ]

    */

    if (data.length === 0)return []

    let clientList    = [], storeList = [], gondolaList = [], shelfList = []
    let currStoreID   = data[0]["storeID"]
    let currGondolaID = data[0]["displayfixtureID"]
    let currShelfID   = data[0]["shelfID"]

    // go through facings, placing them in groupings 
    // according to gondola and shelf
    for (var facing of data) {

      if (facing["shelfID"]!== currShelfID){
        gondolaList.push(shelfList)
        shelfList=[]
      }

      if (facing["displayfixtureID"]!== currGondolaID){
        storeList.push(gondolaList)
        gondolaList=[]
      }

      if (facing["storeID"]!== currStoreID){
        clientList.push(storeList)
        storeList=[]
      }
      currStoreID = facing["storeID"]
      currGondolaID = facing["displayfixtureID"]
      currShelfID = facing["shelfID"]
      shelfList.push(facing)
    }

    gondolaList.push(shelfList)
    storeList.push(gondolaList)
    clientList.push(storeList)
    return clientList
  }



  const fetchPlanogramsHandler = useCallback(async () => {
    setIsloading(true);
    setError(null);
    try {
      // const url = 'https://apiserver.sungalcorp.synology.me/dbGet_displaymatrixforweb?filter=storeID=7'
      const url = 'https://apiserver.sungalcorp.synology.me/dbGet_displaymatrixforweb'
      const response = await fetch(url);

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }
      const POGfacings =  await response.json();
      console.log("POGfacings =",POGfacings)      
      let POGdata = transformPOGData(POGfacings)

    setPlanogram(POGdata);

    } catch (error) {
      setError(error.message);
    }
    setIsloading(false);
  }, []);

  useEffect(() => {
    fetchPlanogramsHandler();
  }, [fetchPlanogramsHandler]);





  let content = <p>Found no planograms.</p>;

  if (planogram.length > 0) {
    //content = <Store gondolas={planogram} />;
    content = <Client stores={planogram} />;

  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <div>


      <div style={{backgroundColor:'#ff0000'}}>
      <img  
            className="logo_img" 
            src={logoImage}
            alt = 'LogoImg'
            /> 


      <img  
            className="sungal_img" 
            src={SungalImage} 
            alt = 'SungalImg'
            /> 

      <div className="percentage-tag-notice" style={{backgroundColor: getPercentColor('10%')}}>{'<20%'}</div> 
      <div className="percentage-tag-notice" style={{backgroundColor: getPercentColor('30%')}}>{'20%-35%'}</div> 
      <div className="percentage-tag-notice" style={{backgroundColor: getPercentColor('40%')}}>{'35%-55%'}</div> 
      <div className="percentage-tag-notice" style={{backgroundColor: getPercentColor('60%')}}>{'55%-85%'}</div> 
      <div className="percentage-tag-notice" style={{backgroundColor: getPercentColor('90%')}}>{'85%-90%'}</div> 
      <div className="percentage-tag-notice" style={{backgroundColor: getPercentColor('99%')}}>{'90%-100%'}</div> 

      </div>


      <section>
        {content}
      </section>

    </div>
  )

}

// export default App;
export default LivingPOG;
